    handleFileUpload(event) {
      const file = event.target.files[0];
      if (!file) return;

      // Solo permitir tipos de imagen
      const allowedTypes = ['image/png', 'image/jpeg', 'image/jpg', 'image/svg+xml', 'image/gif', 'image/webp'];
      if (!allowedTypes.includes(file.type)) {
        alert('‚ùå Por favor selecciona solo archivos de imagen.\n\nFormatos permitidos: PNG, JPG, JPEG, SVG, GIF, WEBP');
        // Limpiar el input
        event.target.value = '';
        return;
      }
      if (file.size > 10 * 1024 * 1024) {
        alert('‚ùå La imagen es demasiado grande.\n\nM√°ximo 10MB permitido.');
        // Limpiar el input
        event.target.value = '';
        return;
      }
      this.uploadedFile = file;
      this.designFile = file;
      console.log('üì∏ Imagen seleccionada:', file.name, 'Tipo:', file.type, 'Tama√±o:', this.formatFileSize(file.size));
    },
    // Formatear tama√±o de archivo
    formatFileSize(bytes) {
      if (!bytes) return 'N/A';
      if (bytes === 0) return '0 Bytes';
      const k = 1024;
      const sizes = ['Bytes', 'KB', 'MB', 'GB'];
      const i = Math.floor(Math.log(bytes) / Math.log(k));
      return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
    },
    // Funci√≥n para convertir File a Base64
    fileToBase64(file) {
      return new Promise((resolve, reject) => {
        const reader = new FileReader();
        reader.readAsDataURL(file);
        reader.onload = () => resolve(reader.result);
        reader.onerror = error => reject(error);
      });
    },
