  methods: {
    // Animación del carrusel
    animateCarousel() {
      if (this.isCarouselPaused) return;
      const move = () => {
        this.carouselPosition -= this.carouselSpeed;
        const slideWidth = 320;
        if (Math.abs(this.carouselPosition) >= slideWidth * this.worksData.length) {
