    pauseCarousel() {
      this.isCarouselPaused = !this.isCarouselPaused;
      if (!this.isCarouselPaused) {
        this.animateCarousel();
      } else {
        cancelAnimationFrame(this.animationId);
      }
    },
    // Métodos del formulario de diseño
